import React, { useEffect, useState } from "react";

import "./App.css";
import SearchIcon from "./search.svg";
import MovieCard from "./MovieCard";

const API_URL = "http://www.omdbapi.com?apikey=4a78df0b";

const App = () => {
  const searchMovies = async (title) => {
    const response = await fetch(`${API_URL}&s=${title}`);
    const data = await response.json();
    console.log(data);
    return data;
  };

  const [movies, setMovies] = useState([]);

  useEffect(() => {
    setMovies(searchMovies("Naruto"));
  }, []);

  return (
    <div className="app">
      <h1>MovieLand</h1>

      <div className="search">
        <input
          placeholder="Search for movies"
          value="Naurto"
          onChange={() => {}}
        />
        <img src={SearchIcon} alt="Search" onClick={() => {}} />
      </div>

      <div className="container">
        {}
        {/* <MovieCard movie={movies[0]} /> */}
      </div>
    </div>
  );
};

const movie = {
  Poster:
    "https://m.media-amazon.com/images/M/MV5BZGFiMWFhNDAtMzUyZS00NmQ2LTljNDYtMmZjNTc5MDUxMzViXkEyXkFqcGdeQXVyNjAwNDUxODI@._V1_SX300.jpg",
  Title: "Naruto: Shippuden",
  Type: "series",
  Year: "2007–2017",
  imdbID: "tt0988824",
};

export default App;
