import { useState } from "react";
import "./App.css";

function Person(props) {
  return (
    <>
      <h1>Name: {props.name || "John"}</h1>
      <h1>Last Name: {props.lastName || "Doe"}</h1>
      <h1>Age: {props.age || 30}</h1>
    </>
  );
}

function App() {
  const [num, numSet] = useState(0);
  numSet = (flag) => {
    flag ? (num += 1) : (num -= 1);
  };
  return (
    <div className="App">
      <button onClick={() => numSet(0)}>-</button>
      <h1>{num}</h1>
      <button onClick={() => numSet(1)}>+</button>
    </div>
  );
}

export default App;
